import React, { useEffect, useRef, useState } from 'react';
import '../styles/style.css';
import ChatbotIcon from '../assets/chatbot-toggler.png';
import ZSIcon from '../assets/ZS_Associates.png';

// Hardcoded answers & suggestions (customize as needed)
const HARDCODED_ANSWERS = {
  "where can i find top 10 gainer prescriber over time?": "Top 10 Gainer Prescribers can be found in the Performance Dossier.",
  "what is formulary status?": "Formulary Status is the 'MMIT Pharmacy field which shows Preferred/Covered combined with PA/ST Restrictions.",
  "what are the number of current monthly suggestion kpi?": "It is the 'Count of monthly suggestions (Call and RTE) for a prescriber.",
  "which dossier gives a detailed analysis about the payors?": "You can find detailed analysis about Payor data in the Payor Highlights dossier.",
  "where can i find explanations about different kpis?": "Explanations and Calculation of each and every KPI can be found in the Glossary dossier.",
  "what is mkt % lis?": "Mkt % LIS is the Percentage of claims where claim type is 'PAID' and channel is 'Medicare' and 'Medicare D', and OPC = $0 - $12 and LIS patient flag = LIS-DE , LIS LTC, LIS-NON-DE, LIS-UNKNOWN for Rolling 3M.",
  "which universes do we show in accounts calculation?": "We show three universes Veeva Aligned, Call Plan/DMCP and a combined Veeva Aligned + Call Plan/DMCP universes.",
  "where can i find trx sales trends overtime?": "The sales trends for Retail and Non Retail sales can be found in the Performance Dossier.",
  "are any physicians' sales dropped at a geo level?": "PDRP stands for Prescriber Data Restriction Program. Any prescriber who opts into the PDRP has their sales reported as zero. Sales from prescribers who have opted into the PDRP are included in the geography aggregation. If a geographic area has fewer than three actual PDRP participants, additional prescribers are randomly masked to simulate actual PDRP participation. Any prescriber masked as a PDRP in the R4W period remains masked for all other time periods."
};

const HARDCODED_SUGGESTIONS = [
  "Which universes do we show in Accounts calculation?",
  "Where can I find TRX Sales trends overtime?",
  "Where can I find top 10 Gainer Prescriber over time?",
  "What is Formulary Status?",
  "What are the number of current monthly suggestion KPIs?",
  "Which dossier gives a detailed analysis about the Payors?",
  "Where can I find explanations about different KPIs?",
  "What is MKT % LIS?",
  "Are any physicians' sales dropped at a geo level?"
];

const GENERIC_GREETINGS = [
  "hi", "hello", "hey", "how are you", "good morning", "good afternoon", "good evening",
  "hiya", "yo", "hii", "hello there", "hey there", "hi there"
];

function isGreeting(text) {
  const cleaned = text.trim().toLowerCase();
  return GENERIC_GREETINGS.some(g => cleaned === g || cleaned.startsWith(g + ' '));
}

function formatSnowflakeResponse(responseText) {
  try {
    let json = typeof responseText === 'string' ? JSON.parse(responseText) : responseText;
    if (json.output) {
      return {
        type: "agent2",
        output: json.output,
        sources: json.sources || []
      };
    }
    const columns =
      (json.resultSetMetaData && json.resultSetMetaData.rowType && json.resultSetMetaData.rowType.map(col => col.name)) ||
      (json.rowType && json.rowType.map(col => col.name)) ||
      (json.columns && json.columns.map(col => col.name));
    const data = json.data;
    if (Array.isArray(columns) && columns.length > 0 && Array.isArray(data)) {
      return { type: "table", columns, data };
    }
    if (json.error) return { type: "error", value: "âŒ Error: " + json.error };
    if (json.code && json.code !== "000000" && (!json.message || !json.message.toLowerCase().includes("executed successfully"))) {
      return { type: "error", value: `âŒ Error: ${json.code} - ${json.message}` };
    }
    if (json.message) return { type: "output", value: json.message };
    return { type: "output", value: "No data found." };
  } catch {
    return { type: "error", value: responseText || "No response from backend." };
  }
}

const ChatBot = () => {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem('chatMessages');
    if (saved) return JSON.parse(saved);
    return [{ role: 'assistant', text: 'Hello ðŸ‘‹! How may I assist you?' }];
  });
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [clarificationSuggestions, setClarificationSuggestions] = useState(null); // list of suggestions
  const [clarifyOrigUserMsg, setClarifyOrigUserMsg] = useState(""); // save the original user input to display above suggestions

  const chatRef = useRef();
  const inputRef = useRef();

  // Scroll to bottom on messages update
  useEffect(() => {
    chatRef.current?.scrollTo({ top: chatRef.current.scrollHeight, behavior: 'smooth' });
  }, [messages, isTyping, isOpen]);

  // === MAIN SEND FUNCTION ===
  const handleSendMessage = async (userMessage) => {
    if (!userMessage.trim()) return;
    setInput('');
    setIsTyping(true);

    // -- Hardcoded greeting/answer short-circuit --
    if (isGreeting(userMessage)) {
      setTimeout(() => {
        setIsTyping(false);
        const greetingResponse = "Hello! ðŸ‘‹ How can I assist you with your field insights or business data today?";
        const updatedMessages = [...messages, { role: 'user', text: userMessage }, { role: 'assistant', text: greetingResponse }];
        setMessages(updatedMessages);
        localStorage.setItem('chatMessages', JSON.stringify(updatedMessages));
      }, 600);
      return;
    }
    const cleaned = userMessage.trim().toLowerCase();
    const matchedKey = Object.keys(HARDCODED_ANSWERS).find(k => cleaned.includes(k));
    if (matchedKey) {
      const hardcodedAnswer = HARDCODED_ANSWERS[matchedKey];
      setTimeout(() => {
        setIsTyping(false);
        const updatedMessages = [...messages, { role: 'user', text: userMessage }, { role: 'assistant', text: hardcodedAnswer }];
        setMessages(updatedMessages);
        localStorage.setItem('chatMessages', JSON.stringify(updatedMessages));
      }, 650);
      return;
    }

    // --- 1. Call CLARIFY endpoint for suggestions ---
    const response = await fetch('http://localhost:4000/api/clarify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userMessage })
    });
    const { response: clarifyText } = await response.json();

    // Try to parse suggestions as JSON; if not, fallback to line-split
    let suggestions = [];
    try {
      const asJSON = JSON.parse(clarifyText);
      if (Array.isArray(asJSON)) suggestions = asJSON;
    } catch {
      // fallback: find lines that look like suggestions (skip line numbers if present)
      suggestions = clarifyText
        .split('\n')
        .map(line => line.replace(/^\d+\.\s*/, '').trim())
        .filter(line => !!line && line.length > 3);
    }

    setIsTyping(false);
    setClarificationSuggestions(suggestions.length > 0 ? suggestions : null);
    setClarifyOrigUserMsg(userMessage);

    // Show the assistant's clarifying question as a message:
    const newMsgs = [...messages, { role: 'user', text: userMessage }];
    if (clarifyText && clarifyText.length > 0)
      newMsgs.push({ role: 'assistant', text: clarifyText });
    setMessages(newMsgs);
    localStorage.setItem('chatMessages', JSON.stringify(newMsgs));
  };

  // === When user clicks a suggestion or enters a finalized query ===
  const handleClarifySuggestion = async (finalQuery) => {
    if (!finalQuery.trim()) return;
    setIsTyping(true);
    setClarificationSuggestions(null);

    // 2. Pass to CUSTOM_AGENT2 (Snowflake)
    const prompt = finalQuery.replace(/'/g, "''");
    const sfBody = { statement: `CALL CUSTOM_AGENT2('${prompt}')` };
    const sfResponse = await fetch('http://localhost:4000/api/snowflake', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(sfBody)
    });
    const sfText = await sfResponse.text();
    const formatted = formatSnowflakeResponse(sfText);

    setIsTyping(false);
    // Show both the selection and the result in chat
    const newMsgs = [
      ...messages,
      { role: 'user', text: finalQuery },
      { role: 'assistant', text: formatted }
    ];
    setMessages(newMsgs);
    localStorage.setItem('chatMessages', JSON.stringify(newMsgs));
    setInput('');
  };

  // --- Typing Indicator Component ---
  function TypingIndicator() {
    return <span className="typing-indicator"><span></span><span></span><span></span></span>;
  }

  // Render assistant output with agent2 results, tables, etc.
  const renderChatBubbleContent = (msg) => {
    if (typeof msg.text === "object" && msg.text !== null) {
      const obj = msg.text;
      if (obj.type === "table") {
        return (
          <table className="snowflake-table">
            <thead><tr>{obj.columns.map((h, i) => <th key={i}>{h}</th>)}</tr></thead>
            <tbody>
              {obj.data.length === 0 ? (
                <tr><td colSpan={obj.columns.length} style={{ textAlign: 'center', color: '#888' }}>(no results)</td></tr>
              ) : obj.data.map((row, ridx) => (
                <tr key={ridx}>
                  {row.map((cell, cidx) => (<td key={cidx}>{cell}</td>))}
                </tr>
              ))}
            </tbody>
          </table>
        );
      }
      if (obj.type === "output") { return <div>{obj.value}</div>; }
      if (obj.type === "error") { return <span style={{ color: "#b91c1c", fontWeight: 500 }}>{obj.value}</span>; }
      if (obj.type === "agent2") {
        return (
          <div style={{ marginBottom: 6 }}>
            <div style={{ marginBottom: 10, fontWeight: 500, color: "#5233c0" }}>{obj.output}</div>
            {obj.sources && obj.sources.length > 0 && (
              <details style={{ marginTop: 6 }}>
                <summary style={{ cursor: "pointer", color: "#7c3aed", fontSize: 13, fontWeight: 600 }}>Source(s) used</summary>
                <div style={{ fontSize: 13, color: "#2b2553", marginTop: 4 }}>
                  {obj.sources.map((src, i) =>
                    <div key={i} style={{ marginBottom: 3 }}>
                      {src.metadata && src.metadata.length > 0 && (
                        <div>
                          Table:&nbsp;
                          <span style={{ fontFamily: "monospace" }}>
                            {src.metadata.map(m => m.Table).join(", ")}
                          </span>
                        </div>
                      )}
                      {src.tool_name && (<div>Tool: <span style={{ fontFamily: "monospace" }}>{src.tool_name}</span></div>)}
                      {src.tool_type && (<div>Type: <span style={{ fontFamily: "monospace" }}>{src.tool_type}</span></div>)}
                    </div>
                  )}
                </div>
              </details>
            )}
          </div>
        );
      }
      return <pre>{JSON.stringify(obj.value, null, 2)}</pre>;
    }
    return (msg.text || "").split('\n').map((line, i) => (<div key={i}>{line}</div>));
  };

  return (
    <div>
      <button className="chatbot-toggler modern-toggler"
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle chatbot"
        style={{ position: 'fixed', right: '20px', bottom: '20px', zIndex: 10000 }}>
        {isOpen ? 'âœ–' : <img src={ChatbotIcon} alt="Chatbot" style={{ width: 48, height: 48 }} />}
      </button>
      {isOpen && (
        <div className="chatbot modern-chatbot">
          <ul className="chatbox" ref={chatRef}>
            {messages.map((msg, idx) => (
              <li
                key={idx}
                className={`chat ${msg.role === 'user' ? 'outgoing' : 'incoming'}`}>
                <div className={`chat-bubble ${msg.role}`}>
                  {renderChatBubbleContent(msg)}
                </div>
              </li>
            ))}
            {isTyping && (
              <li className="chat incoming">
                <div className="chat-bubble assistant">
                  <TypingIndicator />
                </div>
              </li>
            )}
          </ul>

          {/* --- CLARIFICATION SUGGESTIONS PANEL --- */}
          {clarificationSuggestions && clarificationSuggestions.length > 0 && (
            <div className="clarification-suggestions" style={{ padding: 18 }}>
              <div style={{ fontWeight: 600, marginBottom: 7 }}>
                Please select or refine your query:
              </div>
              {clarificationSuggestions.map((s, i) => (
                <button
                  key={i}
                  className="suggestion-button"
                  style={{ margin: "3px 5px 7px 0" }}
                  onClick={() => handleClarifySuggestion(s)}
                >{s}</button>
              ))}
              <div style={{ margin: "12px 0 0 0" }}>
                <input
                  placeholder="Or type your final query..."
                  style={{ width: "70%", marginRight: 6 }}
                  value={input}
                  onChange={e => setInput(e.target.value)}
                  onKeyDown={e => e.key === 'Enter' && handleClarifySuggestion(input)}
                  disabled={isTyping}
                />
                <button
                  className="send-button"
                  onClick={() => handleClarifySuggestion(input)}
                  disabled={isTyping || !input.trim()}
                >Send</button>
              </div>
            </div>
          )}

          {/* Input shown only when not in suggestion-select mode */}
          {!clarificationSuggestions && (
            <div className="chat-input">
              <textarea
                ref={inputRef}
                placeholder="Ask me anything..."
                value={input}
                onChange={(e) => setInput(e.target.value)}
                onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSendMessage(input)}
                rows={1}
                className="chat-input-textarea"
                style={{ overflow: 'hidden', resize: 'none' }}
                disabled={isTyping}
              />
              <button
                onClick={() => handleSendMessage(input)}
                title="Send message"
                className={`send-button${input.trim() ? " has-text" : ""}`}
                disabled={isTyping}
              >
                <svg height="22" width="22" viewBox="0 0 24 24" fill="none" style={{ display: "block" }}>
                  <path d="M4 20L20 12L4 4V10L16 12L4 14V20Z" fill="currentColor" />
                </svg>
              </button>
            </div>
          )}
          <footer className="chatbot-footer">
            Powered by <img src={ZSIcon} alt="ZS Associates" />
          </footer>
        </div>
      )}
    </div>
  );
};

export default ChatBot;
