import React, { useEffect, useRef, useState } from 'react';
import '../styles/style.css';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const chatRef = useRef();

  useEffect(() => {
    if (isOpen) {
      const greeting = `Hello üëã! I'm your Field Assistant. How can I help you today?`;
      setMessages([{ role: 'assistant', text: greeting }]);
    }
  }, [isOpen]);

  const scrollToBottom = () => {
    setTimeout(() => {
      if (chatRef.current) {
        chatRef.current.scrollTop = chatRef.current.scrollHeight;
      }
    }, 100);
  };

  const sendToSnowflakeAPI = async (message) => {
    try {
      const res = await fetch('http://localhost:4000/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ query: message })
      });

      if (!res.ok) throw new Error('Request failed');

      const data = await res.json();
      const rows = data.rows || [];
      if (rows.length === 0) return 'üîç No results found.';

      const formatted = rows.map((row, i) => `üîπ ${JSON.stringify(row, null, 2)}`).join('\n\n');
      return formatted;
    } catch (err) {
      console.error('API error:', err);
      return '‚ö†Ô∏è Unable to connect to the backend. Please try again later.';
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    const userMessage = input.trim();
    const newMessages = [...messages, { role: 'user', text: userMessage }];
    setMessages(newMessages);
    setInput('');
    scrollToBottom();

    const pendingResponse = { role: 'assistant', text: '‚è≥ Working on your request...' };
    setMessages([...newMessages, pendingResponse]);

    const response = await sendToSnowflakeAPI(userMessage);
    setMessages([...newMessages, { role: 'assistant', text: response }]);
    scrollToBottom();
  };

  return (
    <>
      <button
        className="chatbot-toggler modern-toggler"
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle chatbot">
        {isOpen ? '‚úñ' : 'ü§ñ'}
      </button>

      {isOpen && (
        <div
          className="chatbot modern-chatbot"
          style={{ position: 'fixed', right: '20px', bottom: '80px', maxWidth: '400px', width: '90vw', height: '70vh', borderRadius: '16px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', backgroundColor: '#fff', display: 'flex', flexDirection: 'column', overflow: 'hidden', transition: 'all 0.3s ease', zIndex: 9999 }}
        >
          <header style={{ backgroundColor: '#003366', color: '#fff', padding: '16px', fontSize: '16px', fontWeight: '500', borderTopLeftRadius: '16px', borderTopRightRadius: '16px' }}>
            ORION <strong style={{ color: '#94d6f2' }}>Field Assistant</strong>
          </header>
          <ul className="chatbox" ref={chatRef} style={{ flex: 1, padding: '16px', overflowY: 'auto' }}>
            {messages.map((msg, idx) => (
              <li key={idx} className={`chat ${msg.role === 'user' ? 'outgoing' : 'incoming'}`} style={{ marginBottom: '12px' }}>
                <p style={{ background: msg.role === 'user' ? '#003366' : '#f0f0f0', color: msg.role === 'user' ? '#fff' : '#000', padding: '10px 14px', borderRadius: '10px', maxWidth: '75%', display: 'inline-block' }}>{msg.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input" style={{ display: 'flex', padding: '12px', borderTop: '1px solid #ddd', alignItems: 'center' }}>
            <textarea
              placeholder="Type your question here..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSend()}
              style={{ flex: 1, resize: 'none', padding: '10px', borderRadius: '8px', border: '1px solid #ccc', fontSize: '14px', marginRight: '10px' }}
            ></textarea>
            <button onClick={handleSend} title="Send message" style={{ backgroundColor: '#003366', color: '#fff', border: 'none', padding: '10px 14px', borderRadius: '8px', cursor: 'pointer' }}>
              Send
            </button>
          </div>
        </div>
      )}
    </>
  );
};

export default ChatBot;
