import React, { useEffect, useRef, useState } from 'react';
import '../styles/style.css';
import ChatbotIcon from '../assets/chatbot-toggler.png';
import ZSIcon from '../assets/ZS_Associates.png';

// --- HELPER FUNCTIONS ---
function isClarificationNeeded(text) {
  const clarificationPhrases = [
    "I need some additional information",
    "Could you please specify",
    "missing information",
    "can you clarify",
    "could you provide",
    "please provide"
  ];
  return clarificationPhrases.some(p => text.toLowerCase().includes(p.toLowerCase()));
}

function isReadyToSend(text) {
  return text.toLowerCase().includes("is that correct") ||
         text.toLowerCase().includes("is this correct") ||
         text.toLowerCase().includes("ready to submit") ||
         text.toLowerCase().includes("should i send") ||
         text.toLowerCase().includes("can i send");
}

function formatSnowflakeResponse(responseText) {
  try {
    let json = JSON.parse(responseText);

    const columns =
      (json.resultSetMetaData && json.resultSetMetaData.rowType && json.resultSetMetaData.rowType.map(col => col.name)) ||
      (json.rowType && json.rowType.map(col => col.name)) ||
      (json.rowtype && json.rowtype.map(col => col.name)) ||
      (json.columns && json.columns.map(col => col.name));
    const data = json.data;
    if (Array.isArray(columns) && columns.length > 0 && Array.isArray(data)) {
      return {
        type: "table",
        columns,
        data
      };
    }

    if (json.error) return { type: "error", value: "‚ùå Error: " + json.error };
    if (
      json.code &&
      json.code !== "000000" &&
      (!json.message || !json.message.toLowerCase().includes("executed successfully"))
    ) {
      return { type: "error", value: `‚ùå Error: ${json.code} - ${json.message}` };
    }
    if (json.message) return { type: "output", value: json.message };

    return { type: "output", value: "No data found." };
  } catch {
    return { type: "error", value: responseText || "No response from backend." };
  }
}

// --- TYPING INDICATOR ---
function TypingIndicator() {
  return (
    <span className="typing-indicator">
      <span></span><span></span><span></span>
    </span>
  );
}

// --- MAIN COMPONENT ---
const ChatBot = () => {
  const [messages, setMessages] = useState(() => {
    const saved = localStorage.getItem('chatMessages');
    return saved ? JSON.parse(saved) : [{ role: 'assistant', text: 'Hello üëã! How may I assist you?' }];
  });
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [isTyping, setIsTyping] = useState(false);
  const [clarifyMode, setClarifyMode] = useState(false);
  const [clarifyHistory, setClarifyHistory] = useState([]);
  const [awaitingConfirm, setAwaitingConfirm] = useState(false);
  const [clarifiedQuery, setClarifiedQuery] = useState("");
  const chatRef = useRef();

  useEffect(() => {
    chatRef.current?.scrollTo({ top: chatRef.current.scrollHeight, behavior: 'smooth' });
  }, [messages, isTyping, isOpen]);

  // --- SENDING A MESSAGE ---
  const handleSendMessage = async (userMessage) => {
    if (!userMessage.trim()) return;
    setInput('');
    setIsTyping(true);

    // --- CLARIFICATION FLOW ---
    if (clarifyMode) {
      const newHistory = [
        ...clarifyHistory,
        { role: "user", content: userMessage }
      ];

      const response = await fetch('http://localhost:4000/clarify', {
        method: 'POST',
        headers: { 'Content-Type': 'application/json' },
        body: JSON.stringify({
          userMessage,
          conversationHistory: newHistory
        })
      });
      const { response: aiText } = await response.json();

      setIsTyping(false);

      const newMessages = [
        ...messages,
        { role: "user", text: userMessage },
        { role: "assistant", text: aiText }
      ];
      setMessages(newMessages);
      setClarifyHistory([
        ...newHistory,
        { role: "assistant", content: aiText }
      ]);
      localStorage.setItem('chatMessages', JSON.stringify(newMessages));

      if (isReadyToSend(aiText)) {
        setAwaitingConfirm(true);
        setClarifiedQuery(aiText);
      }
      return;
    }

    // --- INITIAL MESSAGE TO CLARIFICATION ENDPOINT ---
    const response = await fetch('http://localhost:4000/api/clarify', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify({ userMessage, conversationHistory: [] })
    });
    const { response: aiText } = await response.json();

    setIsTyping(false);
    let newMessages = [...messages, { role: 'user', text: userMessage }, { role: 'assistant', text: aiText }];
    setMessages(newMessages);
    localStorage.setItem('chatMessages', JSON.stringify(newMessages));
    setClarifyHistory([{ role: "user", content: userMessage }, { role: "assistant", content: aiText }]);

    if (isClarificationNeeded(aiText)) {
      setClarifyMode(true);
    } else if (isReadyToSend(aiText)) {
      setAwaitingConfirm(true);
      setClarifiedQuery(aiText);
    } else {
      setClarifyMode(false);
    }
  };

  // --- SEND CLARIFIED QUERY TO CORTEX/SNOWFLAKE ---
  const handleConfirmSend = async () => {
    setIsTyping(true);
    setAwaitingConfirm(false);
    setClarifyMode(false);

    let body = { statement: clarifiedQuery };
    const response = await fetch('http://localhost:4000/api/snowflake', {
      method: 'POST',
      headers: { 'Content-Type': 'application/json' },
      body: JSON.stringify(body)
    });
    const responseText = await response.text();
    const formatted = formatSnowflakeResponse(responseText);

    setIsTyping(false);
    const newMessages = [
      ...messages,
      { role: "assistant", text: "Sending clarified query to report system..." },
      { role: "assistant", text: formatted }
    ];
    setMessages(newMessages);
    localStorage.setItem('chatMessages', JSON.stringify(newMessages));
    setClarifiedQuery("");
    setClarifyHistory([]);
  };

  // --- RENDER CHAT BUBBLES ---
  const renderChatBubbleContent = (msg) => {
    if (typeof msg.text === "object" && msg.text !== null) {
      const obj = msg.text;
      if (obj.type === "table") {
        return (
          <table className="snowflake-table">
            <thead>
              <tr>
                {obj.columns.map((h, i) => <th key={i}>{h}</th>)}
              </tr>
            </thead>
            <tbody>
              {obj.data.length === 0 ? (
                <tr>
                  <td colSpan={obj.columns.length} style={{ textAlign: 'center', color: '#888' }}>(no results)</td>
                </tr>
              ) : obj.data.map((row, ridx) => (
                <tr key={ridx}>
                  {row.map((cell, cidx) => (
                    <td key={cidx} style={{whiteSpace: 'pre-wrap'}}>
                      {(() => {
                        try {
                          if (typeof cell === "string" && (cell.trim().startsWith('[') || cell.trim().startsWith('{'))) {
                            const parsed = JSON.parse(cell);
                            if (Array.isArray(parsed)) {
                              return (
                                <ul style={{ paddingLeft: '18px', margin: 0 }}>
                                  {parsed.map((item, idx) => <li key={idx}>{item}</li>)}
                                </ul>
                              );
                            }
                            if (typeof parsed === 'object') {
                              return <pre>{JSON.stringify(parsed, null, 2)}</pre>;
                            }
                          }
                          return cell;
                        } catch {
                          return cell;
                        }
                      })()}
                    </td>
                  ))}
                </tr>
              ))}
            </tbody>
          </table>
        );
      }
      if (obj.type === "output") {
        return <div>{obj.value}</div>;
      }
      if (obj.type === "error") {
        return <span style={{ color: "#b91c1c", fontWeight: 500 }}>{obj.value}</span>;
      }
      return <pre>{JSON.stringify(obj.value, null, 2)}</pre>;
    }
    return (msg.text || "").split('\n').map((line, i) => (
      <div key={i}>{line}</div>
    ));
  };

  // --- MAIN RENDER ---
  return (
    <div>
      <button
        className="chatbot-toggler modern-toggler"
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle chatbot"
        style={{ position: 'fixed', right: '20px', bottom: '20px', zIndex: 10000 }}>
        {isOpen ? '‚úñ' : <img src={ChatbotIcon} alt="Chatbot" style={{ width: 48, height: 48 }} />}
      </button>
      {isOpen && (
        <div className="chatbot modern-chatbot">
          <header className="chatbot-header">
            <span className="header-title">
              Field Insights<span className="header-title-gradient"> Assistant</span>
            </span>
          </header>
          <ul className="chatbox" ref={chatRef}>
            {messages.map((msg, idx) => (
              <li
                key={idx}
                className={`chat ${msg.role === 'user' ? 'outgoing' : 'incoming'}`}
                style={{justifyContent: msg.role === 'user' ? 'flex-end' : 'flex-start'}}
              >
                <div className={`chat-bubble ${msg.role}`}>
                  {renderChatBubbleContent(msg)}
                </div>
              </li>
            ))}
            {isTyping && (
              <li className="chat incoming">
                <div className="chat-bubble assistant">
                  <TypingIndicator />
                </div>
              </li>
            )}
          </ul>
          {/* Clarification confirmation box */}
          {awaitingConfirm && clarifyMode && (
            <div className="clarify-confirm-box" style={{textAlign: "center", margin: 10}}>
              <button
                className="send-button"
                onClick={handleConfirmSend}
              >
                ‚úÖ Yes, send to report
              </button>
              <button
                className="send-button"
                onClick={() => {
                  setAwaitingConfirm(false);
                  setClarifyMode(true);
                }}
                style={{marginLeft: 8}}
              >
                ‚ùå No, I want to change
              </button>
            </div>
          )}
          <div className="chat-input">
            <textarea
              placeholder="Ask me anything..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSendMessage(input)}
              rows={1}
              className="chat-input-textarea"
              style={{overflow: 'hidden', resize: 'none'}}
              disabled={isTyping || (awaitingConfirm && clarifyMode)}
            />
            <button
              onClick={() => handleSendMessage(input)}
              title="Send message"
              className={`send-button${input.trim() ? " has-text" : ""}`}
              disabled={isTyping || (awaitingConfirm && clarifyMode)}
            >
              <svg height="22" width="22" viewBox="0 0 24 24" fill="none" style={{display:"block"}}>
                <path d="M4 20L20 12L4 4V10L16 12L4 14V20Z" fill="currentColor"/>
              </svg>
            </button>
          </div>
          <footer className="chatbot-footer">
            Powered by <img src={ZSIcon} alt="ZS Associates" />
          </footer>
        </div>
      )}
    </div>
  );
};

export default ChatBot;
