import React, { useEffect, useRef, useState } from 'react';
import '../styles/style.css';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [position, setPosition] = useState({ x: 25, y: 25 });
  const [dragging, setDragging] = useState(false);
  const [rel, setRel] = useState(null);
  const chatRef = useRef();
  const buttonRef = useRef();

  useEffect(() => {
    if (isOpen) {
      const greeting = `Hello ðŸ‘‹! I'm your Field Assistant. How can I help you today?`;
      setMessages([{ role: 'assistant', text: greeting }]);
    }
  }, [isOpen]);

  const scrollToBottom = () => {
    setTimeout(() => {
      if (chatRef.current) {
        chatRef.current.scrollTop = chatRef.current.scrollHeight;
      }
    }, 100);
  };

  const sendToSnowflakeAPI = async (message) => {
    try {
      const res = await fetch('http://localhost:4000/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ query: message })
      });

      if (!res.ok) throw new Error('Request failed');

      const data = await res.json();
      const rows = data.rows || [];
      if (rows.length === 0) return 'ðŸ” No results found.';

      const formatted = rows.map((row, i) => `ðŸ”¹ ${JSON.stringify(row, null, 2)}`).join('\n\n');
      return formatted;
    } catch (err) {
      console.error('API error:', err);
      return 'âš ï¸ Unable to connect to the backend. Please try again later.';
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    const userMessage = input.trim();
    const newMessages = [...messages, { role: 'user', text: userMessage }];
    setMessages(newMessages);
    setInput('');
    scrollToBottom();

    const pendingResponse = { role: 'assistant', text: 'â³ Working on your request...' };
    setMessages([...newMessages, pendingResponse]);

    const response = await sendToSnowflakeAPI(userMessage);
    setMessages([...newMessages, { role: 'assistant', text: response }]);
    scrollToBottom();
  };

  const handleMouseDown = (e) => {
    if (e.button !== 0) return;
    const rect = buttonRef.current.getBoundingClientRect();
    setRel({ x: e.pageX - rect.left, y: e.pageY - rect.top });
    setDragging(true);
    e.stopPropagation();
    e.preventDefault();
  };

  const handleMouseMove = (e) => {
    if (!dragging) return;
    setPosition({ x: e.pageX - rel.x, y: e.pageY - rel.y });
    e.stopPropagation();
    e.preventDefault();
  };

  const handleMouseUp = () => {
    setDragging(false);
  };

  useEffect(() => {
    if (dragging) {
      document.addEventListener('mousemove', handleMouseMove);
      document.addEventListener('mouseup', handleMouseUp);
    } else {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    }
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [dragging]);

  return (
    <>
      <button
        className="chatbot-toggler"
        onClick={() => setIsOpen(!isOpen)}
        onMouseDown={handleMouseDown}
        ref={buttonRef}
        style={{ position: 'absolute', left: position.x, top: position.y }}
        aria-label="Toggle chatbot"
      >
        {isOpen ? 'âœ–' : 'ðŸ’¬'}
      </button>

      {isOpen && (
        <div className="chatbot">
          <header>
            <h3>ðŸ¤– ORION <strong style={{ color: '#94d6f2' }}>Field Assistant</strong></h3>
          </header>
          <ul className="chatbox" ref={chatRef}>
            {messages.map((msg, idx) => (
              <li key={idx} className={`chat ${msg.role === 'user' ? 'outgoing' : 'incoming'}`}>
                <p>{msg.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Type your question here..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSend()}
            ></textarea>
            <span className="material-symbols-rounded" onClick={handleSend} title="Send message">send</span>
          </div>
        </div>
      )}
    </>
  );
};

export default ChatBot;
