import React, { useEffect, useRef, useState } from 'react';
import '../styles/style.css';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [isTyping, setIsTyping] = useState(false);
  const suggestions = ["Show top 5 territories", "List HCP performance", "Fetch TRx summary"];
  const clearChat = () => setMessages([]);

  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [darkMode, setDarkMode] = useState(false);
  const chatRef = useRef();
  const inputRef = useRef();

  useEffect(() => {
    if (isOpen) {
      const greeting = `Hello 👋! I'm your Field Assistant. How can I help you today?`;
      setMessages([{ role: 'assistant', text: greeting }]);
    }
  }, [isOpen]);

  useEffect(() => {
    chatRef.current?.scrollTo({ top: chatRef.current.scrollHeight, behavior: 'smooth' });
  }, [messages]);

  const sendToSnowflakeAPI = async (message) => {
    try {
      const res = await fetch('http://localhost:4000/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ query: message })
      });

      if (!res.ok) throw new Error('Request failed');

      const data = await res.json();
      const rows = data.rows || [];
      if (rows.length === 0) return '🔍 No results found.';

      const formatted = rows.map((row, i) => `🔹 ${JSON.stringify(row, null, 2)}`).join('\n\n');
      return formatted;
    } catch (err) {
      console.error('API error:', err);
      return '⚠️ Unable to connect to the backend. Please try again later.';
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    const userMessage = input.trim();
    const timestamp = new Date().toLocaleTimeString();
    const newMessages = [...messages, { role: 'user', text: userMessage, time: timestamp }];
    setMessages(newMessages);
    setInput('');

    const pendingResponse = { role: 'assistant', text: '⏳ Working on your request...', time: new Date().toLocaleTimeString() };
    setMessages([...newMessages, pendingResponse]);

    setIsTyping(true);
    const response = await sendToSnowflakeAPI(userMessage);
    setIsTyping(false);
    setMessages([...newMessages, { role: 'assistant', text: response, time: new Date().toLocaleTimeString() }]);
  };

  const toggleTheme = () => setDarkMode(prev => !prev);

  return (
    <>
      <button
        className="chatbot-toggler modern-toggler"
        onClick={() => setIsOpen(!isOpen)}
        aria-label="Toggle chatbot"
        style={{ position: 'fixed', right: '20px', bottom: '20px', zIndex: 10000 }}>
        {isOpen ? '✖' : '🤖'}
      </button>

      {isOpen && (
        <div
          className={`chatbot modern-chatbot ${darkMode ? 'dark-mode' : ''}`}
          style={{ position: 'fixed', right: '20px', bottom: '80px', maxWidth: '400px', width: '90vw', height: '70vh', borderRadius: '16px', boxShadow: '0 4px 12px rgba(0,0,0,0.1)', backgroundColor: darkMode ? '#1e1e1e' : '#fff', display: 'flex', flexDirection: 'column', overflow: 'hidden', transition: 'all 0.3s ease', zIndex: 9999 }}
        >
          <header style={{ backgroundColor: '#003366', color: '#fff', padding: '16px', fontSize: '16px', fontWeight: '500', borderTopLeftRadius: '16px', borderTopRightRadius: '16px', display: 'flex', justifyContent: 'space-between', alignItems: 'center' }}>
            <span>🤖 ORION <strong style={{ color: '#94d6f2' }}>Field Assistant</strong></span>
            <div>
              <button onClick={toggleTheme} style={{ background: 'transparent', border: 'none', color: '#fff', cursor: 'pointer', marginRight: '8px' }}>
                {darkMode ? '🌞' : '🌙'}
              </button>
              <button onClick={clearChat} style={{ background: 'transparent', border: '1px solid #fff', color: '#fff', borderRadius: '4px', padding: '4px 8px', cursor: 'pointer' }}>Clear</button>
            </div>
          </header>
          <ul className="chatbox" ref={chatRef} style={{ flex: 1, padding: '16px', overflowY: 'auto' }}>
            {messages.map((msg, idx) => (
              <li key={idx} className={`chat ${msg.role === 'user' ? 'outgoing' : 'incoming'}`} style={{ marginBottom: '12px' }}>
                <p style={{ background: msg.role === 'user' ? '#003366' : '#f0f0f0', color: msg.role === 'user' ? '#fff' : '#000', padding: '10px 14px', borderRadius: '10px', maxWidth: '75%', display: 'inline-block' }}>
                  <span style={{ fontWeight: 'bold', marginRight: '6px' }}>{msg.role === 'user' ? '🧑' : '🤖'}</span>{msg.text}<br/><small style={{ fontSize: '11px', opacity: 0.7 }}>{msg.time}</small></p>
              </li>
            ))}
            {isTyping && (
              <li className="chat incoming" style={{ marginBottom: '12px' }}>
                <p style={{ background: '#f0f0f0', color: '#000', padding: '10px 14px', borderRadius: '10px', maxWidth: '75%', display: 'inline-block', fontStyle: 'italic' }}>Field Assistant is thinking...</p>
              </li>
            )}
          </ul>

          <div className="chat-input" style={{ display: 'flex', padding: '12px', borderTop: '1px solid #ddd', alignItems: 'center' }}>
            <textarea
              ref={inputRef}
              placeholder="Type your question here..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSend()}
              rows={1}
              style={{ flex: 1, resize: 'none', padding: '10px', borderRadius: '8px', border: '1px solid #ccc', fontSize: '14px', marginRight: '10px', lineHeight: '1.4em', overflow: 'hidden' }}
              onInput={(e) => {
                e.target.style.height = 'auto';
                e.target.style.height = `${e.target.scrollHeight}px`;
              }}
            ></textarea>
            <button onClick={handleSend} title="Send message" style={{ backgroundColor: '#003366', color: '#fff', border: 'none', padding: '10px 14px', borderRadius: '8px', cursor: 'pointer' }}>
              Send
            </button>
          </div>
          <div className="suggestions" style={{ padding: '8px 16px', borderTop: '1px solid #eee', display: 'flex', flexWrap: 'wrap', gap: '8px' }}>
            {suggestions.map((s, i) => (
              <button
                key={i}
                onClick={() => { setInput(s); setTimeout(() => handleSend(), 0); }}
                style={{ backgroundColor: '#e6f0ff', color: '#003366', border: 'none', borderRadius: '20px', padding: '6px 12px', fontSize: '12px', cursor: 'pointer' }}>
                {s}
              </button>
            ))}
          </div>
        </div>
      )}
    </>
  );
};

export default ChatBot;
