import React, { useEffect, useRef, useState, useCallback } from 'react';
import '../styles/style.css';

const ChatBot = () => {
  const [messages, setMessages] = useState([]);
  const [input, setInput] = useState('');
  const [isOpen, setIsOpen] = useState(false);
  const [position, setPosition] = useState(() => {
  const savedPos = localStorage.getItem('chatbot-position');
  return savedPos ? JSON.parse(savedPos) : { x: 25, y: window.innerHeight - 100 };
});
  const [dragging, setDragging] = useState(false);
  const [rel, setRel] = useState(null);
  const [size, setSize] = useState(() => {
  const savedSize = localStorage.getItem('chatbot-size');
  return savedSize ? JSON.parse(savedSize) : { width: 400, height: 500 };
});
  const chatRef = useRef();
  const buttonRef = useRef();
  const resizeRef = useRef();
  const [resizing, setResizing] = useState(false);
  const [resizeStart, setResizeStart] = useState(null);

  useEffect(() => {
    if (isOpen) {
      const greeting = `Hello 👋! I'm your Field Assistant. How can I help you today?`;
      setMessages([{ role: 'assistant', text: greeting }]);
    }
  }, [isOpen]);

  const scrollToBottom = () => {
    setTimeout(() => {
      if (chatRef.current) {
        chatRef.current.scrollTop = chatRef.current.scrollHeight;
      }
    }, 100);
  };

  const sendToSnowflakeAPI = async (message) => {
    try {
      const res = await fetch('http://localhost:4000/query', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json'
        },
        body: JSON.stringify({ query: message })
      });

      if (!res.ok) throw new Error('Request failed');

      const data = await res.json();
      const rows = data.rows || [];
      if (rows.length === 0) return '🔍 No results found.';

      const formatted = rows.map((row, i) => `🔹 ${JSON.stringify(row, null, 2)}`).join('\n\n');
      return formatted;
    } catch (err) {
      console.error('API error:', err);
      return '⚠️ Unable to connect to the backend. Please try again later.';
    }
  };

  const handleSend = async () => {
    if (!input.trim()) return;
    const userMessage = input.trim();
    const newMessages = [...messages, { role: 'user', text: userMessage }];
    setMessages(newMessages);
    setInput('');
    scrollToBottom();

    const pendingResponse = { role: 'assistant', text: '⏳ Working on your request...' };
    setMessages([...newMessages, pendingResponse]);

    const response = await sendToSnowflakeAPI(userMessage);
    setMessages([...newMessages, { role: 'assistant', text: response }]);
    scrollToBottom();
  };

  const handleMouseDown = (e) => {
    if (e.button !== 0) return;
    const rect = buttonRef.current.getBoundingClientRect();
    setRel({ x: e.pageX - rect.left, y: e.pageY - rect.top });
    setDragging(true);
    e.stopPropagation();
    e.preventDefault();
  };

  const handleMouseMove = useCallback((e) => {
    if (dragging) {
      const newPos = { x: e.pageX - rel.x, y: e.pageY - rel.y };
      setPosition(newPos);
      localStorage.setItem('chatbot-position', JSON.stringify(newPos));
      e.stopPropagation();
      e.preventDefault();
    }
    if (resizing && resizeStart) {
      const dx = e.clientX - resizeStart.x;
      const dy = e.clientY - resizeStart.y;
      setSize(prev => {
        const newSize = { width: Math.max(300, prev.width - dx), height: Math.max(300, prev.height + dy) };
        localStorage.setItem('chatbot-size', JSON.stringify(newSize));
        return newSize;
      });
      setResizeStart({ x: e.clientX, y: e.clientY });
    }
  }, [dragging, rel, resizing, resizeStart]);

  const handleMouseUp = useCallback(() => {
    setDragging(false);
    setResizing(false);
    setResizeStart(null);
  }, []);

  const handleResizeMouseDown = (e) => {
    setResizing(true);
    setResizeStart({ x: e.clientX, y: e.clientY });
    e.stopPropagation();
    e.preventDefault();
  };

  useEffect(() => {
    document.addEventListener('mousemove', handleMouseMove);
    document.addEventListener('mouseup', handleMouseUp);
    return () => {
      document.removeEventListener('mousemove', handleMouseMove);
      document.removeEventListener('mouseup', handleMouseUp);
    };
  }, [handleMouseMove, handleMouseUp]);

  return (
    <>
      <button
        className="chatbot-toggler"
        onClick={() => setIsOpen(!isOpen)}
        onMouseDown={handleMouseDown}
        ref={buttonRef}
        style={{ position: 'absolute', left: position.x, top: position.y }}
        aria-label="Toggle chatbot"
      >
        {isOpen ? '✖' : '💬'}
      </button>

      {isOpen && (
        <div
          className="chatbot"
          style={{ width: size.width, height: size.height, position: 'absolute', left: position.x, top: position.y, transition: 'all 0.3s ease' }}
        >
          <header>
            <h3>🤖 ORION <strong style={{ color: '#94d6f2' }}>Field Assistant</strong></h3>
          </header>
          <ul className="chatbox" ref={chatRef}>
            {messages.map((msg, idx) => (
              <li key={idx} className={`chat ${msg.role === 'user' ? 'outgoing' : 'incoming'}`}>
                <p>{msg.text}</p>
              </li>
            ))}
          </ul>
          <div className="chat-input">
            <textarea
              placeholder="Type your question here..."
              value={input}
              onChange={(e) => setInput(e.target.value)}
              onKeyDown={(e) => e.key === 'Enter' && !e.shiftKey && handleSend()}
            ></textarea>
            <span className="material-symbols-rounded" onClick={handleSend} title="Send message">send</span>
          </div>
          <div
            ref={resizeRef}
            className="resize-handle"
            onMouseDown={handleResizeMouseDown}
            style={{ position: 'absolute', bottom: 0, left: 0, width: '12px', height: '12px', background: '#003366', cursor: 'sw-resize' }} title="Resize chatbot"
          ></div>
        </div>
      )}
    </>
  );
};

export default ChatBot;
